using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;[ExecuteInEditMode]public class FPSText : MonoBehaviour {    public TextMesh TextRenderer;    public Text TextCanvas;   // UnityStandardAssets.Utility.FPSCounter FPS_counter;    void OnEnable () {        TextRenderer = GetComponent<TextMesh>();       // FPS_counter.	}		// Update is called once per frame	void Update ()    {        if(TextRenderer && TextCanvas)        TextRenderer.text = TextCanvas.text;	}}